,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'FUNCTION render_nprogress(p_dynamic_action IN apex_plugin.t_dynamic_action,',
'                          p_plugin         IN apex_plugin.t_plugin)',
'  RETURN apex_plugin.t_dynamic_action_render_result IS',
'  --',
'  v_start_stop        VARCHAR2(100) := p_dynamic_action.attribute_01;',
'  --',
'  l_result apex_plugin.t_dynamic_action_render_result;',
'  --',
'  v_nprogress_js_call VARCHAR2(100);',
'BEGIN  ',
'  IF apex_application.g_debug THEN',
'    apex_plugin_util.debug_dynamic_action(p_plugin         => p_plugin,',
'                                          p_dynamic_action => p_dynamic_action);',
'  END IF;',
'  --',
'  -- CSS',
'  apex_css.add_file(p_name      => ''nprogress.min'',',
'                    p_directory => p_plugin.file_prefix);',
'  --',
'  -- JS',
'  apex_javascript.add_library(p_name           => ''nprogress.min'',',
'                              p_directory      => p_plugin.file_prefix,',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'',
'  IF v_start_stop = ''START'' THEN',
'    v_nprogress_js_call := ''function() {NProgress.start();}'';',
'  ELSIF v_start_stop = ''STOP'' THEN',
'    v_nprogress_js_call := ''function() {NProgress.done();}'';',
'  END IF;',
'  --',
'  l_result.javascript_function := v_nprogress_js_call;',
'  l_result.attribute_01        := v_start_stop;',
'  --',
'  RETURN l_result;',
'  --',
'END render_nprogress;'))
,p_render_function=>'render_nprogress'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_version_identifier=>'1.0'
,p_about_url=>'https://github.com/drumbeg/apex-nprogress'
);
